// import React from "react";
// import * as ReactDOMClient from "react-dom/client";
// import ReactDOM from "react-dom";
// import App from "./app";
// import "./css/main.css";

// const app = ReactDOMClient.createRoot(document.getElementById("main"));
// app.render(<App />);

import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  userAdd = "";
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      name2: "",
      name: "",
    };
    this.AddProg = this.AddProg.bind(this);
  }
  render() {
    return (
      <div>
        <form
          onSubmit={this.AddProg}
          ref={(element) => (this.myForm = element)}
        >
          <label>Your name: </label>
          <input
            type="text"
            onChange={(e) => this.setState({ name2: e.target.value })}
          ></input>
          <button type="submit">Add</button>
        </form>
        <br />
        <br />
        <form>
          <label>Sort by text: </label>
          <input type="text"></input>
          <button type="submit">Sort</button>
        </form>
        <br />
        <br />
        {this.state.users.map((el, index) => (
          <li key={index}>{el}</li>
        ))}
      </div>
    );
  }
  AddProg = (em) => {
    em.preventDefault();
    this.myForm.reset();
    this.setState({ name: this.state.name2 });
    this.setState({ name2: "" });

    const AllUsers = this.state.users;
    this.setState({ users: [] }, () => {
      if (this.state.name2 != "") {
        this.setState({ users: [...AllUsers, this.state.name] });
      } else this.setState({ users: [...AllUsers] });
    });
    console.log(this.state.users);
    console.log(this.state.name);
  };
}
const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);
