import React from "react";
import GameBoard from "./gameBoard";

class Play extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cssOne: "",
      cssTwo: "",
    };
  }

  //  dateProps  -  Імена іграків та вибрані ними знаки (Х/О)

  dateProps = {
    nameOne: "",
    nameTwo: "",
    selectOneX: "",

    selectOne: "",
    selectTwo: "",

    cssOne: "",
    cssTwo: "",
  };
  shiftDate = {
    step: 1,
    oneOne: "",
    oneTwo: "",
    oneThree: "",

    twoOne: "",
    twoTwo: "",
    twoThree: "",

    threeOne: "",
    threeTwo: "",
    threeThree: "",
  };

  render() {
    this.selectUsers();
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.dateProps.nameOne}</div>
            <div>
              <button id="btnLeft" className={this.state.cssOne}>
                {this.dateProps.selectOne}
              </button>
            </div>
          </div>
          <div>
            <button className="newGame btn" onClick={this.newGame}>
              New Games
            </button>
          </div>
          <div className="userName nameRight">
            <div>
              <button id="btnRight" className={this.state.cssTwo}>
                {this.dateProps.selectTwo}
              </button>
            </div>
            <div>{this.dateProps.nameTwo}</div>
          </div>
        </div>

        {/* Поле гри */}

        <div className="playPlace">
          <div id="oneOne" onClick={this.logicPro}>
            {this.state.oneOne}
          </div>
          <div id="oneTwo" onClick={this.logicPro}>
            {this.state.oneTwo}
          </div>
          <div id="oneThree" onClick={this.logicPro}>
            {this.state.oneThree}
          </div>

          <div id="twoOne" onClick={this.logicPro}>
            {this.state.twoOne}
          </div>
          <div id="twoTwo" onClick={this.logicPro}>
            {this.state.twoTwo}
          </div>
          <div id="twoThree" onClick={this.logicPro}>
            {this.state.twoThree}
          </div>

          <div id="threeOne" onClick={this.logicPro}>
            {this.state.threeOne}
          </div>
          <div id="threeTwo" onClick={this.logicPro}>
            {this.state.threeTwo}
          </div>
          <div id="threeThree" onClick={this.logicPro}>
            {this.state.threeThree}
          </div>
        </div>

        <div className="futer">
          <div className="futerMain">
            <div className="futerName">
              <div>{this.dateProps.nameOne}</div>
              <div>{this.dateProps.nameTwo}</div>
            </div>
            <div className="futerVictory">
              <div>
                <div>Victory</div>
                <div>0</div>
                <div>0</div>
              </div>
              <button onClick={this.resetVictory}>RESET</button>
            </div>
          </div>
          <div className="futerExit">
            <button>EXIT</button>
          </div>
        </div>
      </div>
    );
  }
  selectUsers = () => {
    this.dateProps.nameOne = this.props.infoUsers.nameOne;
    this.dateProps.nameTwo = this.props.infoUsers.nameTwo;
    this.dateProps.selectOneX = this.props.infoUsers.selectOneX;

    if (this.dateProps.selectOneX === "X") {
      this.dateProps.selectOne = "X";
      this.dateProps.selectTwo = "O";
      if (this.shiftDate.step % 2 !== 0) {
        this.dateProps.cssOne = "selButton";
        this.dateProps.cssTwo = "empty";
      } else {
        this.dateProps.cssOne = "empty";
        this.dateProps.cssTwo = "selButton";
      }
    } else if (this.dateProps.selectOneX === "O") {
      this.dateProps.selectOne = "O";
      this.dateProps.selectTwo = "X";
      if (this.shiftDate.step % 2 !== 0) {
        this.dateProps.cssOne = "empty";
        this.dateProps.cssTwo = "selButton";
      }
      if (this.shiftDate.step % 2 === 0) {
        this.dateProps.cssOne = "selButton";
        this.dateProps.cssTwo = "empty";
      }
    }
  };

  saveInfo = (info) => {
    this.shiftDate.step = info.step;

    this.shiftDate.oneOne = info.oneOne;
    this.shiftDate.oneTwo = info.oneTwo;
    this.shiftDate.oneThree = info.oneThree;

    this.shiftDate.twoOne = info.twoOne;
    this.shiftDate.twoTwo = info.twoTwo;
    this.shiftDate.twoThree = info.twoThree;

    this.shiftDate.threeOne = info.threeOne;
    this.shiftDate.threeTwo = info.threeTwo;
    this.shiftDate.threeThree = info.threeThree;

    this.selectUsers();
  };

  logicPro = (e) => {
    if (e.target.id === "oneOne" && this.state.oneOne === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ oneOne: "0" });
      } else {
        this.setState({ oneOne: "X" });
      }
    }
    if (e.target.id === "oneTwo" && this.state.oneTwo === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ oneTwo: "0" });
      } else {
        this.setState({ oneTwo: "X" });
      }
    }
    if (e.target.id === "oneThree" && this.state.oneThree === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ oneThree: "0" });
      } else {
        this.setState({ oneThree: "X" });
      }
    }
    if (e.target.id === "twoOne" && this.state.twoOne === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ twoOne: "0" });
      } else {
        this.setState({ twoOne: "X" });
      }
    }
    if (e.target.id === "twoTwo" && this.state.twoTwo === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ twoTwo: "0" });
      } else {
        this.setState({ twoTwo: "X" });
      }
    }
    if (e.target.id === "twoThree" && this.state.twoThree === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ twoThree: "0" });
      } else {
        this.setState({ twoThree: "X" });
      }
    }
    if (e.target.id === "threeOne" && this.state.threeOne === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ threeOne: "0" });
      } else {
        this.setState({ threeOne: "X" });
      }
    }
    if (e.target.id === "threeTwo" && this.state.threeTwo === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ threeTwo: "0" });
      } else {
        this.setState({ threeTwo: "X" });
      }
    }
    if (e.target.id === "threeThree" && this.state.threeThree === "") {
      if (this.state.step % 2 === 0) {
        this.setState({ threeThree: "0" });
      } else {
        this.setState({ threeThree: "X" });
      }
    }
    this.setState({ step: this.state.step + 1 });
    this.props.saveInfo(this.state);
  };
  newGame = () => {};
  resetVictory = () => {};
}
export default Play;
