import React from "react";
import GameBoard from "./gameBoard";

class Play extends React.Component {
  text = {};
  constructor(props) {
    super(props);
    this.state = {
      playerOne: "",
      playerTwo: "",
    };
  }
  render() {
    this.selectUsers();
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.props.infoUsers.nameOne}</div>
            <div>
              <button className="btnLeft">{this.state.playerOne}</button>
            </div>
          </div>
          <div>
            <button className="newGame btn">New Games</button>
          </div>
          <div className="userName nameRight">
            <div>
              <button className="btnRight">{this.state.playerTwo}</button>
            </div>
            <div>{this.props.infoUsers.nameTwo}</div>
          </div>
        </div>
        <div>
          <GameBoard infoUsers={this.props.infoUsers} />
        </div>
        <div className="futer">
          <div className="futerMain">
            <div className="futerName">
              <div>{this.props.infoUsers.nameOne}</div>
              <div>{this.props.infoUsers.nameTwo}</div>
            </div>
            <div className="futerVictory">
              <div>
                <div>Victory</div>
                <div>0</div>
                <div>0</div>
              </div>
              <button>RESET</button>
            </div>
          </div>
          <div className="futerExit">
            <button>EXIT</button>
          </div>
        </div>
      </div>
    );
  }
  selectUsers = () => {
    if (this.props.infoUsers.selectOneX === "X") {
      this.setState({ playerOne: "X", playerTwo: "O" });
    } else if (this.props.infoUsers.selectOneX === "O") {
      this.setState({ playerOne: "O", playerTwo: "X" });
    }
  };
}
export default Play;
