import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class TimeButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      NowTime: "Click here to see a time",
    };
    this.TimeChange = this.TimeChange.bind(this);
  }

  render() {
    return <h1 onClick={this.state.TimeChange}>{this.state.NowTime}</h1>;
  }

  TimeChange = () => {
    const time = new Date();
    this.setState({ NowTime: time.toLocaleTimeString() });
  };
}
export default TimeButton;

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<TimeButton />);
