import React from "react";
import * as ReactDOMClient from "react-dom/client";
import ReactDOM from "react-dom";
import App from "./app";
import "./css/main.css";

const app = ReactDOMClient.createRoot(document.getElementById("main"));
app.render(<App />);

class Nam extends React.Component {
  state = {
    val: 0,
  };

  render() {
    return (
      <div>
        <button type="button" onClick={this.increaseCounter}>
          -1
        </button>
        <label>{this.state.val}</label>
        <button type="button" onClick={this.decrementCounter}>
          +1
        </button>
      </div>
    );
  }
  increaseCounter = () => {
    this.setState({ val: this.state.val - 1 });
  };
  decrementCounter = () => {
    this.setState({ val: this.state.val + 1 });
  };
}
// const link = ReactDOMClient.createRoot(document.getElementById("test"));
// link.render(<Nam />);
const link = document.getElementById("test");
ReactDOM.render(Nam, link);
