import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menu: ["Main", "Servise", "For as", "Contact"],
      action: 0,
    };
  }

  render() {
    return (
      <div>
        <header>
          {this.state.menu.map((el, index) => {
            let cssDiv = this.state.action === index ? "menuStyle" : "";
          })}
          <div onClick={cssDiv}>{this.state.main}</div>
          <div onClick={() => this.setState({ action: [this.state.servise] })}>
            {this.state.servise}
          </div>
          <div onClick={() => this.setState({ action: [this.state.forAs] })}>
            {this.state.forAs}
          </div>
          <div onClick={() => this.setState({ action: [this.state.contact] })}>
            {this.state.contact}
          </div>
        </header>
        <div>Selected: {this.state.action}</div>
      </div>
    );
  }
}
export default App;
