import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      textUnder: "",
    };
    this.PrassBut = this.PrassBut.bind(this);
  }
  render() {
    return (
      <form>
        <input type="button" value="But1" name={1} onClick={this.PrassBut} />
        <input type="button" value="But2" name={2} onClick={this.PrassBut} />
        <input type="button" value="But3" name={3} onClick={this.PrassBut} />
        <br />
        <br />
        <label>{this.state.textUnder}</label>
      </form>
    );
  }
  PrassBut = ({ target }) => {
    this.setState({ textUnder: "Action butt1" });
  };
}
export default Main;

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Main />);
