import React from "react";

class Play extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOne: "",
      selectTwo: "",
    };
    this.selectUsers = this.selectUsers.bind(this);
  }

  render() {
    this.selectUsers();
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.props.infoUsers.nameOne}</div>
            <div>
              <button className="btnLeft">x</button>
            </div>
          </div>
          <div>
            <button className="newGame btn">New Games</button>
          </div>
          <div className="userName nameRight">
            <div>
              <button className="btnRight">x</button>
            </div>
            <div>{this.props.infoUsers.nameTwo}</div>
          </div>
        </div>
      </div>
    );
  }
  selectUsers = () => {
    if (this.props.infoUsers.selectOneX === 1) {
      this.setState({ selectOne: "X" });
      this.setState({ selectTwo: "O" });
    } else {
      this.setState({ selectOne: "O" });
      this.setState({ selectTwo: "X" });
    }
  };
}
export default Play;
