import React from "react";
import GameBoard from "./gameBoard";

class Play extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cssOne: "",
      cssTwo: "",
      selectOne: "",
      selectTwo: "",

      step: 1,

      oneOne: "",
      oneTwo: "",
      oneThree: "",

      twoOne: "",
      twoTwo: "",
      twoThree: "",

      threeOne: "",
      threeTwo: "",
      threeThree: "",
    };
  }
  render() {
    this.selectUsers(this.props.infoUsers.selectOneX);
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.props.infoUsers.nameOne}</div>
            <div>
              <button id="btnLeft" className={this.state.cssOne}>
                {this.state.selectOne}
              </button>
            </div>
          </div>
          <div>
            <button className="newGame btn">New Games</button>
          </div>
          <div className="userName nameRight">
            <div>
              <button id="btnRight" className={this.state.cssTwo}>
                {this.state.selectTwo}
              </button>
            </div>
            <div>{this.props.infoUsers.nameTwo}</div>
          </div>
        </div>
        <div>
          <GameBoard infoGame={this.state} saveInfo={this.saveInfo} />
        </div>
        <div className="futer">
          <div className="futerMain">
            <div className="futerName">
              <div>{this.props.infoUsers.nameOne}</div>
              <div>{this.props.infoUsers.nameTwo}</div>
            </div>
            <div className="futerVictory">
              <div>
                <div>Victory</div>
                <div>0</div>
                <div>0</div>
              </div>
              <button onClick={this.resetBorder}>RESET</button>
            </div>
          </div>
          <div className="futerExit">
            <button>EXIT</button>
          </div>
        </div>
      </div>
    );
  }
  selectUsers = (el) => {
    if (el === "X") {
      this.setState({ selectOne: "X" });
      this.setState({ selectTwo: "O" });
      this.setState({ cssOne: "selButton" });
      this.setState({ cssTwo: "empty" });
    } else if (el === "O") {
      this.setState({ selectOne: "O" });
      this.setState({ selectTwo: "X" });
      this.setState({ cssOne: "empty" });
      this.setState({ cssTwo: "selButton" });
    }
  };

  resetBorder = () => {};
  saveInfo = (info) => {};
}
export default Play;
