import React from "react";
import GameBoard from "./gameBoard";

class Play extends React.Component {
  text = {};
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     selectOne: "",
  //     selectTwo: "",
  //   };
  //   this.selectUsers = this.selectUsers.bind(this);
  // }

  render() {
    this.selectUsers();
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.props.infoUsers.nameOne}</div>
            <div>
              <button className="btnLeft">{this.text.selectOne}</button>
            </div>
          </div>
          <div>
            <button className="newGame btn">New Games</button>
          </div>
          <div className="userName nameRight">
            <div>
              <button className="btnRight">{this.text.selectTwo}</button>
            </div>
            <div>{this.props.infoUsers.nameTwo}</div>
          </div>
        </div>
        <div>
          <GameBoard />
        </div>
        <div className="futer">
          <div className="futerMain">
            <div className="futerName">
              <div>
                {this.props.infoUsers.nameOne}
                <br />
                {this.props.infoUsers.nameTwo}
              </div>
            </div>
            <div className="futerVictory">
              <div>
                <label>Victory</label>
                <label>0</label>
                <label>0</label>
              </div>
            </div>
          </div>
          <div className="futerExit"></div>
        </div>
      </div>
    );
  }
  selectUsers = () => {
    if (this.props.infoUsers.selectOneX === 1) {
      this.text = { selectOne: "X", selectTwo: "O" };
    } else {
      this.text = { selectOne: "O", selectTwo: "X" };
    }
  };
}
export default Play;
