import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menu: ["Main", "Servise", "For as", "Contact"],
      action: 0,
    };
  }

  render() {
    return (
      <div>
        <header>
          {this.state.menu.map((el, index) => {
            let cssDiv = this.state.action === index ? "menuStyle" : "";
            return (
              <div>
                <div onClick={cssDiv} key={index}></div>
              </div>
            );
          })}
        </header>
        <div>Selected: {this.state.action}</div>
      </div>
    );
  }
}
export default App;
