// import React from "react";
// import * as ReactDOMClient from "react-dom/client";
// import ReactDOM from "react-dom";
// import App from "./app";
// import "./css/main.css";

// const app = ReactDOMClient.createRoot(document.getElementById("main"));
// app.render(<App />);

import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      message: "",
      var: false,
    };
  }
  render() {
    return (
      <>
        <form onSubmit={() => this.setState({ var: !this.state.var })}>
          <label>Name: </label>
          <input
            type="text"
            placeholder="Enter your name"
            onChange={(el) => this.setState({ name: el.target.value })}
          ></input>
          <br />
          <br />
          <label>Your message: </label>
          <br />
          <textarea
            placeholder="Enter your message"
            onChange={(e) => this.setState({ message: e.target.value })}
          ></textarea>
          <br />
          <button type="submit">Enter</button>
        </form>
        <div>
          {this.state.var &&
            ((<h1>{this.state.name}</h1>), (<h3>{this.state.message}</h3>))}
        </div>
      </>
    );
  }
}
export default Prog;

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);
