import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date(),
    };
    // this.ProgramTime = this.ProgramTime.bind(this);
  }
  componentDidMount() {
    this.timer = setInterval(() => this.sec(), 1000);
  }

  // componentWillUnmount() {
  //   clearInterval(this.timer);
  // }

  sec() {
    this.setState({
      date: new Date(),
    });
  }
  render() {
    return <h2>{this.state.date.toLocaleTimeString()}</h2>;
  }
  // ProgramTime = () => {};
}
export default Clock;

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Clock />);
