// import React from "react";
// import * as ReactDOMClient from "react-dom/client";
// import ReactDOM from "react-dom";
// import App from "./app";
// import "./css/main.css";

// const app = ReactDOMClient.createRoot(document.getElementById("main"));
// app.render(<App />);

import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      message: "",
      var: false,
    };
  }
  render() {
    return (
      <>
        <form ref={(el) => (this.myForm = el)}>
          <label htmlFor="name">Name: </label>
          <input
            type="text"
            name="name"
            placeholder="Enter your name"
            onChange={(el) => this.setState({ name: el.target.value })}
          ></input>
          <br />
          <br />
          <label htmlFor="mess">Your message: </label>
          <br />
          <textarea
            name="mess"
            placeholder="Enter your message"
            onChange={(e) => this.setState({ message: e.target.value })}
          ></textarea>
          <br />
          <button type="submit" onClick={this.Sub}>
            Enter
          </button>
        </form>
        {this.state.var && <h3>{this.state.name}</h3>}
        {this.state.var && <h3>{this.state.message}</h3>}
      </>
    );
  }
  Sub = (e) => {
    e.preventDefault();
    this.myForm.reset();
    if (this.state.name !== "" && this.state.message !== "") {
      this.setState({ var: true });
    } else this.setState({ name: "" });
    console.log(this.state.name);
    console.log(this.state.message);
  };
}
export default Prog;

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);
