// import React from "react";
// import * as ReactDOMClient from "react-dom/client";
// import ReactDOM from "react-dom";
// import App from "./app";
// import "./css/main.css";

// const app = ReactDOMClient.createRoot(document.getElementById("main"));
// app.render(<App />);

import React, { Fragment, Component, Suspense } from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

// const Profile = React.lazy(() => import("./components/component"));

class App extends Component {
  state = {
    showProfile: false,
  };

  showProfileToggle = () => {
    this.setState(({ showProfile }) => ({
      showProfile: !showProfile,
    }));
  };

  render() {
    const { showProfile } = this.state;

    return (
      <Fragment>
        <button onClick={this.showProfileToggle}>Toggle Profile</button>
        {showProfile ? (
          <Suspense fallback={<div>Loading...</div>}>
            <Profile />
          </Suspense>
        ) : (
          <h1>Start page</h1>
        )}
      </Fragment>
    );
  }
}

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<App />);
