import React from "react";

class Play extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nameOne: "",
      nameTwo: "",
      selectOneX: false,
      selectOneO: false,
      selectTwoX: false,
      selectTwoO: false,
    };
  }

  render() {
    this.selectUsers();
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.state.nameOne}</div>
            <div>
              <button className="btnLeft">X </button>
            </div>
          </div>
          <div>
            <button className="newGame btn">New Games</button>
          </div>
          <div className="userName nameRight">
            <div>
              <button className="btnRight">X</button>
            </div>
            <div>{this.state.nameTwo}</div>
          </div>
        </div>
      </div>
    );
  }
  selectUsers = () => {
    this.setState({ nameOne: this.props.infoUsers.nameOne });
    this.setState({ nameTwo: this.props.infoUsers.nameTwo });
    this.setState({ selectOneX: date.selectOneX });
    this.setState({ selectOneO: date.selectOneO });
    this.setState({ selectTwoX: date.selectTwoX });
    this.setState({ selectTwoO: date.selectTwoO });
  };
}
export default Play;
