// import React from "react";
// import * as ReactDOMClient from "react-dom/client";
// import ReactDOM from "react-dom";
// import App from "./app";
// import "./css/main.css";

// const app = ReactDOMClient.createRoot(document.getElementById("main"));
// app.render(<App />);

import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      text: "",
    };
  }
  render() {
    return (
      <div>
        <h2>List work</h2>
        <form ref={(el) => (this.MyForm = el)} onSubmit={this.AddText}>
          <input
            type="text"
            onChange={(e) => this.setState({ text: e.target.value })}
          />
        </form>
        <br />
        <br />
        <li>{this.state.text}</li>
      </div>
    );
  }
  AddText = (e) => {
    e.preventDefault();
  };
}

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);

export default Prog;
