import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      message: "",
    };
  }
  render() {
    return (
      <>
        <form>
          <label>Input your name: </label>
          <input ref="InputText" onKeyUp={this.changeText} />
          <br />
          <br />
          <label>Yout text: {this.state.message}</label>
        </form>
      </>
    );
  }
  changeText = (e) => {
    var newText = e.target.value;
    console.log(newText);
  };
}
const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);
