import React from "react";
import App from "C:/HTML_CSS/xo/src/app";

class Users extends React.Component {
  usersAdd = {};
  constructor(props) {
    super(props);
    this.state = {
      nameOne: "",
      nameTwo: "",
      selectOneX: 0,
      selectOneO: 0,
      selectTwoX: 0,
      selectTwoO: 0,
      selectPage: 0,
      cssOneX: "",
      cssOneO: "",
      cssTwoX: "",
      cssTwoO: "",
    };
    this.selectSymbol = this.selectSymbol.bind(this);
    this.shouPlay = this.shouPlay.bind(this);
  }
  render() {
    return (
      <div>
        <div id="InputForm">
          <form>
            <input
              type="text"
              placeholder="Your name"
              onChange={(e) => this.setState({ nameOne: e.target.value })}
            />
            <input
              type="button"
              value="X"
              name="oneX"
              className={this.state.cssOneX}
              onClick={this.selectSymbol}
            />
            <input
              type="button"
              value="O"
              name="oneO"
              className={this.state.cssOneO}
              onClick={this.selectSymbol}
            />
            <br />
            <br />
            <input
              type="text"
              placeholder="Your name"
              onChange={(e) => this.setState({ nameTwo: e.target.value })}
            />
            <input
              type="button"
              value="X"
              name="twoX"
              className={this.state.cssTwoX}
              onClick={this.selectSymbol}
            />
            <input
              type="button"
              value="O"
              name="twoO"
              className={this.state.cssTwoO}
              onClick={this.selectSymbol}
            />
            <br />
            <br />
            <input type="button" value="START" onClick={this.shouPlay} />
          </form>
        </div>
      </div>
    );
  }
  shouPlay = () => {
    this.state.nameOne !== "" && this.state.nameTwo !== ""
      ? this.props.playShou(this.usersAdd)
      : console.log("Error");
  };
  selectSymbol = (el) => {
    if (el.target.name === "oneX" || el.target.name === "twoO") {
      this.setState({ cssOneX: "selectButton" });
      this.setState({ cssTwoO: "selectButton" });
      this.setState({ selectOneX: 1 });
      this.setState({ selectTwoO: 1 });
      this.setState({ selectOneO: 0 });
      this.setState({ selectTwoX: 0 });
      this.setState({ cssOneO: "" });
      this.setState({ cssTwoX: "" });
    }
    if (el.target.name === "oneO" || el.target.name === "twoX") {
      this.setState({ cssOneO: "selectButton" });
      this.setState({ cssTwoX: "selectButton" });
      this.setState({ selectOneO: 1 });
      this.setState({ selectTwoX: 1 });
      this.setState({ selectOneX: 0 });
      this.setState({ selectTwoO: 0 });
      this.setState({ cssOneX: "" });
      this.setState({ cssTwoO: "" });
    }
  };
}
export default Users;
