import React from "react";
import GameBoard from "./gameBoard";

class Play extends React.Component {
  dateProps = {
    nameOne: "",
    nameTwo: "",

    selectOne: "",
    selectTwo: "",
    selectOneX: "",

    cssOne: "",
    cssTwo: "",

    step: 1,

    oneOne: "",
    oneTwo: "",
    oneThree: "",

    twoOne: "",
    twoTwo: "",
    twoThree: "",

    threeOne: "",
    threeTwo: "",
    threeThree: "",
  };
  constructor(props) {
    super(props);
    this.state = {
      users: {
        nameOne: "",
        nameTwo: "",

        selectOne: "",
        selectTwo: "",
        selectOneX: "",

        cssOne: "",
        cssTwo: "",

        step: 1,

        oneOne: "",
        oneTwo: "",
        oneThree: "",

        twoOne: "",
        twoTwo: "",
        twoThree: "",

        threeOne: "",
        threeTwo: "",
        threeThree: "",
      },
    };
    this.selectUsers = this.selectUsers.bind(this);
    this.stapeFun = this.stapeFun.bind(this);
  }

  render() {
    this.selectUsers();
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.state.users.nameOne}</div>
            <div>
              <button id="btnLeft" className={this.state.users.cssOne}>
                {this.state.users.selectOne}
              </button>
            </div>
          </div>
          <div>
            <button className="newGame btn">New Games</button>
          </div>
          <div className="userName nameRight">
            <div>
              <button id="btnRight" className={this.state.users.cssTwo}>
                {this.state.users.selectTwo}
              </button>
            </div>
            <div>{this.state.users.nameTwo}</div>
          </div>
        </div>
        <div>
          {/* <GameBoard playdata={this.state.user} saveInfo={this.saveInfo} /> */}
        </div>
        <div className="futer">
          <div className="futerMain">
            <div className="futerName">
              <div>{this.state.users.nameOne}</div>
              <div>{this.state.users.nameTwo}</div>
            </div>
            <div className="futerVictory">
              <div>
                <div>Victory</div>
                <div>0</div>
                <div>0</div>
              </div>
              <button onClick={this.resetBorder}>RESET</button>
            </div>
          </div>
          <div className="futerExit">
            <button>EXIT</button>
          </div>
        </div>
      </div>
    );
  }
  selectUsers = () => {
    this.dateProps = this.props.infoUsers;

    console.log(this.dateProps);

    // this.setState(this.dateProps);

    // if (this.text.selectOneX === "X") {
    //   this.setState({ selectOne: "X" });
    //   this.setState({ selectTwo: "O" });
    //   this.setState({ cssOne: "selButton" });
    //   this.setState({ cssTwo: "empty" });
    // } else if (this.text.selectOneX === "O") {
    //   this.setState({ selectOne: "O" });
    //   this.setState({ selectTwo: "X" });
    //   this.setState({ cssOne: "empty" });
    //   this.setState({ cssTwo: "selButton" });
    // }
  };

  resetBorder = () => {};
  saveInfo = (info) => {};
}
export default Play;
