import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  usAdd = {};
  constructor(props) {
    super(props);
    this.state = {
      users: [],
    };
    this.AddProg = this.AddProg.bind(this);
  }
  render() {
    return (
      <div>
        <form
          onSubmit={this.AddProg}
          ref={(element) => (this.myForm = element)}
        >
          <label>Your name: </label>
          <input type="text" ref={(input) => (this.input = input)}></input>
          <button type="submit">Add</button>
        </form>
        <br />
        <br />
        <form>
          <label>Sort by text: </label>
          <input type="text"></input>
          <button type="submit">Sort</button>
        </form>
        <br />
        <br />
        {this.state.users.map()}
      </div>
    );
  }
  AddProg = (e) => {
    const userAdd = this.input.value;
    e.preventDefault();
    this.myForm.reset();
    if (userAdd !== "") {
      this.setState({ users: [] }, () => {
        this.setState({ users: [...userAdd] });
      });
    }

    // return <li>{this.state.users}</li>;
    // console.log(this.state.users.join(" "));
  };
}
const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);
