import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  userAdd = "";
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      usersSel: [],
      name: "",
      sel: "",
    };
    this.AddProg = this.AddProg.bind(this);
  }
  render() {
    return (
      <div>
        <form
          onSubmit={this.AddProg}
          ref={(element) => (this.myForm = element)}
        >
          <label>Your name: </label>
          <input
            type="text"
            onChange={(e) => this.setState({ name: e.target.value })}
          ></input>
          <button type="submit">Add</button>
        </form>
        <br />
        <br />
        <form>
          <label>Sort by text: </label>
          <input
            type="text"
            onChange={(el) => this.setState({ sel: el.target.value })}
          ></input>
          <button type="submit" onClick={this.Sort}>
            Find
          </button>
        </form>
        <br />
        <br />
        {this.state.users.map((el, index) => (
          <li key={index}>{el}</li>
        ))}
        <p>Sort</p>
        {this.state.users.map((el, index) => (
          <li key={index}>{el}</li>
        ))}
      </div>
    );
  }
  AddProg = (em) => {
    em.preventDefault();
    this.myForm.reset();

    const AllUsers = this.state.users;
    this.setState({ users: [] }, () => {
      this.setState({ users: [...AllUsers, this.state.name] });
    });
    console.log(this.state.users);
    console.log(this.state.name);
  };
  Sort = (al) => {
    al.preventDefault();

    const shouSort = this.state.users.filter((elem) => {
      return elem.match(this.state.sel);
    });
    this.setState({ usersSel: [shouSort] });
  };
}
const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);
