import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      message: "",
    };
  }
  render() {
    return (
      <>
        <form>
          <label>Input your name: </label>
          <input
            onChange={(el) => this.setState({ message: el.target.value })}
          />
          <br />
          <br />
          <label>Yout text: {this.git}</label>
        </form>
      </>
    );
  }
  git = () => {
    {
      this.state.message;
    }
    // if (this.state.message !== "") return this.state.message;
    // else return "text empty";
  };
}
const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);
