import React from "react";
import GameBoard from "./gameBoard";

class Play extends React.Component {
  text = {
    cssOne: "",
    cssTwo: "",

    step: 1,

    oneOne: "",
    oneTwo: "",
    oneThree: "",

    twoOne: "",
    twoTwo: "",
    twoThree: "",

    threeOne: "",
    threeTwo: "",
    threeThree: "",
  };

  render() {
    this.selectUsers();
    return (
      <div className="playMain ">
        <div className="playHeader ">
          <div className="userName nameLeft">
            <div>{this.props.infoUsers.nameOne}</div>
            <div>
              <button id="btnLeft" className={this.text.cssOne}>
                {this.text.selectOne}
              </button>
            </div>
          </div>
          <div>
            <button className="newGame btn">New Games</button>
          </div>
          <div className="userName nameRight">
            <div>
              <button id="btnRight" className={this.text.cssTwo}>
                {this.text.selectTwo}
              </button>
            </div>
            <div>{this.props.infoUsers.nameTwo}</div>
          </div>
        </div>
        <div>
          <GameBoard info={this.text} />
        </div>
        <div className="futer">
          <div className="futerMain">
            <div className="futerName">
              <div>{this.props.infoUsers.nameOne}</div>
              <div>{this.props.infoUsers.nameTwo}</div>
            </div>
            <div className="futerVictory">
              <div>
                <div>Victory</div>
                <div>0</div>
                <div>0</div>
              </div>
              <button onClick={this.resetBorder}>RESET</button>
            </div>
          </div>
          <div className="futerExit">
            <button>EXIT</button>
          </div>
        </div>
      </div>
    );
  }
  selectUsers = () => {
    if (this.props.infoUsers.selectOneX === "X") {
      this.text = {
        selectOne: "X",
        selectTwo: "O",
        cssOne: "selButton",
        cssTwo: "empty",
      };
    } else if (this.props.infoUsers.selectOneX === "O") {
      this.text = {
        selectOne: "O",
        selectTwo: "X",
        cssTwo: "selButton",
        cssOne: "empty",
      };
    }
  };
  resetBorder = () => {};
}
export default Play;
