import React from "react";
import ReactDOMClient from "react-dom/client";

class NameUser extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "User",
    };
  }
  render() {
    return (
      <form>
        <input
          type="button"
          value="Change user name"
          onClick={() => {
            user = prompt("Uour name");
          }}
        />
        <label>Hello: {this.state.name}</label>
      </form>
    );
  }
}
export default NameUser;

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<NameUser />);
