import React from "react";
import ReactDOMClient from "react-dom/client";
import "./css/main.css";

class Prog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      text: "",
    };
    this.IClicked = this.IClicked.bind(this);
  }
  render() {
    return (
      <div>
        <h2>List work</h2>
        <form ref={(el) => (this.MyForm = el)} onSubmit={this.AddText}>
          <input
            type="text"
            onChange={(e) => this.setState({ text: e.target.value })}
            ref={(a) => (this.Input = a)}
          />
        </form>
        <br />
        <br />
        {this.state.list.map((el, index) => (
          <li key={index} onClick={this.IClicked(el)}>
            {el} {index}
          </li>
        ))}
        <li> {this.Input.value} </li>
      </div>
    );
  }
  AddText = (e) => {
    e.preventDefault();
    this.MyForm.reset();

    const AllList = this.state.list;
    this.setState({ list: [] }, () => {
      this.setState({ list: [...AllList, this.state.text] });
    });
  };
  IClicked = (el) => {
    // this.setState({
    //   list: this.state.list.filter((e) => e.list === el),
    // });
    console.log(el);
  };
}

const link = ReactDOMClient.createRoot(document.getElementById("test"));
link.render(<Prog />);

export default Prog;
